getDocuments.script:
fetch('https://cloud.digitalocean.com/graphql', {
  method: 'POST',
  credentials: 'include',
  headers: { 'Content-Type': 'application/json' },
  body: JSON.stringify({
    operationName: 'listBillingHistory',
    variables: { listBillingHistoryRequest: { page: 1, per_page: 100 } },
    query: `
      query listBillingHistory(
        $listBillingHistoryRequest: ListBillingHistoryRequest
      ) {
        listBillingHistory(ListBillingHistoryRequest: $listBillingHistoryRequest) {
          billing_history {
            description
            amount
            invoice_id
            invoice_uuid
            date
            type
            memo_id
            receipt_id
            __typename
          }
          meta {
            total
            __typename
          }
          __typename
        }
      }    
    `,
  }),
})
  .then((res) => res.json())
  .then((res) =>
    res.data.listBillingHistory.billing_history.filter(
      (item) => !!item.invoice_id,
    ),
  )
  .then((items) =>
    items.map((item) => ({
      id: item.invoice_id,
      date: item.date,
      total: 'USD ' + item.amount,
      invoice_uuid: item.invoice_uuid,
    })),
  )

forEach.script:
fetch('https://cloud.digitalocean.com/graphql', {
  method: 'POST',
  credentials: 'include',
  headers: { 'Content-Type': 'application/json' },
  body: JSON.stringify({
    operationName: 'getInvoicePDF',
    variables: { getInvoicePdfRequest: { invoice_uuid: {{item.invoice_uuid}} } },
    query: `
      query getInvoicePDF($getInvoicePdfRequest: GetInvoicePDFRequest) {
        getInvoicePDF(GetInvoicePDFRequest: $getInvoicePdfRequest) {
          content_type
          filename
          data
          __typename
        }
      }  
    `,
  }),
})
