getDocuments.script:
fetch('https://sentry.io/api/0/customers/{{config.organization-id}}/invoices/', {
  credentials: 'include',
})
  .then((res) => res.json())
  .then((items) =>
    items.map((item) => ({
      id: item.id,
      date: item.dateCreated,
      total: 'USD ' + item.amount / 100,
      url: item.receipt.url,
    })),
  )
